import React from 'react';
import Card from './Card.jsx'

export default function Cards({data, onClose}) {
  
  // acá va tu código
  // tip, podés usar un map
  if(data){
    return (
      <div className='cards'>
        {data.map(c =>(c.name)? <Card
            id={c.id}
            max={c.max}
            min={c.min}
            name={c.name}
            img={c.img}
            onClose={() => onClose(c.id)}
            key={c.id}

          />: null)}
      </div>
    );
  } else { 
    return(
      <div>Sin ciudades</div>
    )
  }
}



