  const handleOnSearch = () =>{
  const input = document.getElementById("searchInput")
  onSearch(input.value);
  input.value="";
  };
  
    return <div className={styles.searchBar}>
    <input className={styles.input} id="searchInput" placeholder="Agregar una cuidad..." 
